///////////////////////////////////////////////
/// \brief diffType
/// \param v
/// \param x
/// \param th
/// \return
///
/// 1 -> v > x + th
/// 2 -> v < x - th
/// 0 -> x - th <= v <= x + th
__device__ __forceinline__ int diffType(const int v, const int x, const int th)
{
    const int diff = x - v;

    return static_cast<int>(diff < -th) + (static_cast<int>(diff > th) << 1);
}







///////////////////////////////////////////////////
/// \brief calcMask
/// \param C
/// \param v
/// \param th
/// \param mask1
/// \param mask2
///
__device__ void calcMask(const uint C[4], const int v, const int th, int& mask1, int& mask2)
{
    mask1 = 0;
    mask2 = 0;

    int d1, d2;


    d1 = diffType(v, C[0] & 0xff, th);
    d2 = diffType(v, C[2] & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 0;
    mask2 |= ((d1 & 2) >> 1) << 0;

    mask1 |= (d2 & 1) << 8;
    mask2 |= ((d2 & 2) >> 1) << 8;


    d1 = diffType(v, C[1] & 0xff, th);
    d2 = diffType(v, C[3] & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 4;
    mask2 |= ((d1 & 2) >> 1) << 4;

    mask1 |= (d2 & 1) << 12;
    mask2 |= ((d2 & 2) >> 1) << 12;


    d1 = diffType(v, (C[0] >> (2 * 8)) & 0xff, th);
    d2 = diffType(v, (C[2] >> (2 * 8)) & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 2;
    mask2 |= ((d1 & 2) >> 1) << 2;

    mask1 |= (d2 & 1) << 10;
    mask2 |= ((d2 & 2) >> 1) << 10;


    d1 = diffType(v, (C[1] >> (2 * 8)) & 0xff, th);
    d2 = diffType(v, (C[3] >> (2 * 8)) & 0xff, th);

    if ((d1 | d2) == 0)
        return;

    mask1 |= (d1 & 1) << 6;
    mask2 |= ((d1 & 2) >> 1) << 6;

    mask1 |= (d2 & 1) << 14;
    mask2 |= ((d2 & 2) >> 1) << 14;


    d1 = diffType(v, (C[0] >> (1 * 8)) & 0xff, th);
    d2 = diffType(v, (C[2] >> (1 * 8)) & 0xff, th);

    /*if ((d1 | d2) == 0)
                return;*/

    mask1 |= (d1 & 1) << 1;
    mask2 |= ((d1 & 2) >> 1) << 1;

    mask1 |= (d2 & 1) << 9;
    mask2 |= ((d2 & 2) >> 1) << 9;


    d1 = diffType(v, (C[0] >> (3 * 8)) & 0xff, th);
    d2 = diffType(v, (C[2] >> (3 * 8)) & 0xff, th);

    /*if ((d1 | d2) == 0)
                return;*/

    mask1 |= (d1 & 1) << 3;
    mask2 |= ((d1 & 2) >> 1) << 3;

    mask1 |= (d2 & 1) << 11;
    mask2 |= ((d2 & 2) >> 1) << 11;


    d1 = diffType(v, (C[1] >> (1 * 8)) & 0xff, th);
    d2 = diffType(v, (C[3] >> (1 * 8)) & 0xff, th);

    /*if ((d1 | d2) == 0)
                return;*/

    mask1 |= (d1 & 1) << 5;
    mask2 |= ((d1 & 2) >> 1) << 5;

    mask1 |= (d2 & 1) << 13;
    mask2 |= ((d2 & 2) >> 1) << 13;



    d1 = diffType(v, (C[1] >> (3 * 8)) & 0xff, th);
    d2 = diffType(v, (C[3] >> (3 * 8)) & 0xff, th);

    mask1 |= (d1 & 1) << 7;
    mask2 |= ((d1 & 2) >> 1) << 7;

    mask1 |= (d2 & 1) << 15;
    mask2 |= ((d2 & 2) >> 1) << 15;
}

// 1 -> v > x + th
// 2 -> v < x - th
// 0 -> not a keypoint
////////////////////////////////////////////
/// \brief isKeyPoint
/// \param mask1
/// \param mask2
/// \return
///
__device__ __forceinline__ bool isKeyPoint(int mask1, int mask2)
{
    return (__popc(mask1) > 8 && (c_table[(mask1 >> 3) - 63] & (1 << (mask1 & 7)))) ||
            (__popc(mask2) > 8 && (c_table[(mask2 >> 3) - 63] & (1 << (mask2 & 7))));
}







///////////////////////////////////////////////////////////////////
/// \brief cornerScore
/// \param C
/// \param v
/// \param threshold
/// \return
///
///
__device__ int cornerScore(const uint C[4], const int v, const int threshold)
{
    // binary search in [threshold + 1, 255]
    int min = threshold + 1;
    int max = 255;

    while (min <= max)
    {
        const int mid = (min + max) >> 1;

        int mask1 = 0;
        int mask2 = 0;

        calcMask(C, v, mid, mask1, mask2);

        int isKp = static_cast<int>(isKeyPoint(mask1, mask2));

        min = isKp * (mid + 1) + (isKp ^ 1) * min;
        max = (isKp ^ 1) * (mid - 1) + isKp * max;
    }

    return min - 1;
}


////////////////////////
/// \brief calcKeypoints
/// \param img
/// \param cols
/// \param rows
/// \param kpLoc
/// \param maxKeypoints
/// \param score
/// \param threshold
/// \param calcScore
///
__global__ void calcKeypoints(const u_int8_t* img,int cols ,int rows,short2* kpLoc, const unsigned int maxKeypoints, int *score, const int threshold,bool calcScore)
{

    const int j = threadIdx.x + blockIdx.x * blockDim.x + 3;
    const int i = threadIdx.y + blockIdx.y * blockDim.y + 3;

    if (i < rows - 3 && j < cols - 3 )
    {
        int v;
        uint C[4] = {0,0,0,0};

        C[2] |= static_cast<uint>(tex_u( img,i - 3, j - 1,cols)) << 8;
        C[2] |= static_cast<uint>(tex_u( img,i - 3, j,cols));
        C[1] |= static_cast<uint>(tex_u( img,i - 3, j + 1,cols)) << (3 * 8);

        C[2] |= static_cast<uint>(tex_u( img,i - 2, j - 2,cols)) << (2 * 8);
        C[1] |= static_cast<uint>(tex_u( img,i - 2, j + 2,cols)) << (2 * 8);

        C[2] |= static_cast<uint>(tex_u( img,i - 1, j - 3,cols)) << (3 * 8);
        C[1] |= static_cast<uint>(tex_u( img,i - 1, j + 3,cols)) << 8;

        C[3] |= static_cast<uint>(tex_u( img,i, j - 3,cols));
        v     = static_cast<int>(tex_u( img,i, j,cols));
        C[1] |= static_cast<uint>(tex_u( img,i, j + 3,cols));

        int d1 = diffType(v, C[1] & 0xff, threshold);
        int d2 = diffType(v, C[3] & 0xff, threshold);

        if ((d1 | d2) == 0)
            return;

        C[3] |= static_cast<uint>(tex_u( img,i + 1, j - 3,cols)) << 8;
        C[0] |= static_cast<uint>(tex_u( img,i + 1, j + 3,cols)) << (3 * 8);

        C[3] |= static_cast<uint>(tex_u( img,i + 2, j - 2,cols)) << (2 * 8);
        C[0] |= static_cast<uint>(tex_u( img,i + 2, j + 2,cols)) << (2 * 8);

        C[3] |= static_cast<uint>(tex_u( img,i + 3, j - 1,cols)) << (3 * 8);
        C[0] |= static_cast<uint>(tex_u( img,i + 3, j,cols));
        C[0] |= static_cast<uint>(tex_u( img,i + 3, j + 1,cols)) << 8;

        int mask1 = 0;
        int mask2 = 0;

        calcMask(C, v, threshold, mask1, mask2);

        if (isKeyPoint(mask1, mask2))
        {
            if (calcScore)
                score[i*cols+ j] = cornerScore(C, v, threshold);

            const unsigned int ind = atomicInc(&g_counter, (unsigned int)(-1));

            if (ind < maxKeypoints)
                kpLoc[ind] = make_short2(j, i);
        }
    }
}


///////////////////////////////////////////////////////////////////////
/// \brief calcKeypoints_gpu
/// \param img
/// \param cols
/// \param rows
/// \param kpLoc
/// \param maxKeypoints
/// \param score
/// \param threshold
/// \return
///
int calcKeypoints_gpu(u_int8_t * img,int cols,int rows, short2* kpLoc, int maxKeypoints, int* score, int threshold)
{
    void* counter_ptr;
    cudaGetSymbolAddress(&counter_ptr, g_counter);

    dim3 block(32, 8);

    dim3 grid;
    grid.x = ceil(cols - 6/ block.x);
    grid.y = ceil(rows - 6/ block.y);

    cudaMemset(counter_ptr, 0, sizeof(unsigned int));

    calcKeypoints<<<grid, block>>>(img,cols,rows, kpLoc, maxKeypoints, score, threshold,true);

    cudaGetLastError();

    cudaDeviceSynchronize();

    unsigned int count;
    cudaMemcpy(&count, counter_ptr, sizeof(unsigned int), cudaMemcpyDeviceToHost);

    return count;
}




///////////////////////////////////////////////////////////////////////
/// \brief nonmaxSuppression
/// \param kpLoc
/// \param count
/// \param scoreMat
/// \param cols
/// \param rows
/// \param locFinal
/// \param responseFinal
///
__global__ void nonmaxSuppression(const short2* kpLoc, int count, const int* scoreMat,int cols,int rows,short2* locFinal, float* responseFinal)
{

    const int kpIdx = threadIdx.x + blockIdx.x * blockDim.x;

    if (kpIdx < count)
    {
        short2 loc = kpLoc[kpIdx];

        int score = tex_i( scoreMat,loc.y, loc.x,cols);

        bool ismax =
                score > tex_i( scoreMat,loc.y - 1, loc.x - 1,cols) &&
                score > tex_i( scoreMat,loc.y - 1, loc.x    ,cols) &&
                score > tex_i( scoreMat,loc.y - 1, loc.x + 1,cols) &&

                score > tex_i( scoreMat,loc.y    , loc.x - 1,cols) &&
                score > tex_i( scoreMat,loc.y    , loc.x + 1,cols) &&

                score > tex_i( scoreMat,loc.y + 1, loc.x - 1,cols) &&
                score > tex_i( scoreMat,loc.y + 1, loc.x    ,cols) &&
                score > tex_i( scoreMat,loc.y + 1, loc.x + 1,cols);

        if (ismax)
        {
            const unsigned int ind = atomicInc(&g_counter, (unsigned int)(-1));

            locFinal[ind] = loc;
            responseFinal[ind] = static_cast<float>(score);
        }
    }

}


////////////////////////////////////////////////////
/// \brief nonmaxSuppression_gpu
/// \param kpLoc
/// \param count
/// \param score
/// \param rows
/// \param cols
/// \param loc
/// \param response
/// \return
///
int nonmaxSuppression_gpu(const short2* kpLoc, int count, int* score,int rows,int cols, short2* loc, float* response)
{
    void* counter_ptr;
    cudaGetSymbolAddress(&counter_ptr, g_counter);

    dim3 block(256);

    dim3 grid;
    grid.x = ceil(count /block.x);

    cudaMemset(counter_ptr, 0, sizeof(unsigned int));
    nonmaxSuppression<<<grid, block>>>(kpLoc, count, score,cols,rows, loc, response);
    cudaGetLastError();

    cudaDeviceSynchronize();

    unsigned int new_count;
    cudaMemcpy(&new_count, counter_ptr, sizeof(unsigned int), cudaMemcpyDeviceToHost) ;

    return new_count;
}


